@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  /* Custom theme options */
  --color-accent: #5a5a5a;
  --font-primary: "Bespoke Serif", serif;
  --font-secondary: "Open Sans", sans-serif;

  /* Base values */
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 8px;
  --border-color: #cbcace;
}

/* Root element (use instead of `body`) */
#page {
  font-family: var(--font-secondary), system-ui, sans-serif;
  color: #222;
  line-height: 1.5;
  font-size: 1.125rem;
  background: navajowhite;
}

/* Elements */
.section-container {
  max-width: 900px;
  margin: 0 auto;
  padding: 4rem 2rem;
}

a.link {
  line-height: 1.25;
  font-weight: 400;
  border-bottom: 2px solid var(--color-accent, rebeccapurple);
  transform: translateY(-2px); /* move link back into place */
  transition: var(--transition, 0.1s border);

  &:hover {
    border-color: transparent;
  }
}

